import React, { Component } from "react";
import { Card, Row, Col, Badge } from "reactstrap";
import pic from "../assets/img/haland.jpeg";
export class PlayerCompare extends Component {
  static get CONTAINER_STYLE() {
    return {
      position: "relative",
      height: "55vh",
      width: "100%",
      display: "flex",
      flex: 1,
      justifyContent: "center",
      alignItems: "middle",
    };
  }

  static get CARD_STYLE() {
    return {
      height: "450px",
      width: "400px",
      textAlign: "middle",
      background: "gradient",
      color: "#FFF",
      fontFamily: "sans-serif",
      fontSize: "20px",
      textTransform: "uppercase",
      borderRadius: "10px",
    };
  }
  static get IMG_STYLE() {
    return {
      position: "relative",
      width: "400px",
      height: "300px",
      margin: "0px 0px 0px 0x",
    };
  }
  static get MODAL_IMG_STYLE() {
    return {
      position: "relative",
      width: "300px",
      height: "300px",
      margin: "20px 40px 0px 88px",
    };
  }
  static get LOGO_IMG_STYLE() {
    return {
      position: "relative",
      width: "72px",
      height: "72px",
      margin: "-20px 40px 0px 200px",
    };
  }
  static get FOOTER_STYLE() {
    return {
      position: "relative",
      height: "70px",
      width: "400px",
      paddingTop: "0px",
      textAlign: "middle",
      background: "#52C0F5",
      color: "#FFF",
      fontFamily: "sans-serif",
      fontSize: "20px",
      textTransform: "uppercase",
      borderRadius: "10px",
      boxSizing: "border-box",
    };
  }
  static get TEXT_STYLE() {
    return {
      position: "relative",
      textAlign: "center",
      padding: "5px ",
      margin: "0px",
      textdecorationcolor: "white",
    };
  }
  render() {
    return (
      <div style={{ height: 700 }}>
        <Row
          style={{
            marginTop: -50,
            alignSelf: "center",
            alignItems: "center",
          }}
        >
          <img alt="..." src={pic} style={PlayerCompare.MODAL_IMG_STYLE}></img>
        </Row>
        <Col style={{ marginTop: -25, marginLeft: 70 }}>
          <h1>
            <Badge color="primary">Price:</Badge>
            50
          </h1>
        </Col>
        <Col style={{ marginLeft: 235, marginTop: -48 }}>
          <h1>
            <Badge color="primary">Position:</Badge>
            50
          </h1>
        </Col>
        <Row className="mt-1">
          <Col style={{ marginTop: 10 }}>
            <h2 style={{ textAlign: "center", fontSize: 16 }}>
              <Badge color="primary"></Badge>{" "}
              <Badge color="info" style={{ fontSize: 28 }}>
                50
              </Badge>
            </h2>
          </Col>
        </Row>
        <Row className="mt-0">
          <img alt="..." src={pic} style={PlayerCompare.LOGO_IMG_STYLE}></img>
        </Row>
        <Row style={{ marginLeft: -10, marginTop: -20 }}>
          <Col>
            <h1>
              <Badge color="primary">Points:</Badge>
              50
            </h1>
          </Col>
          <Col>
            {" "}
            <h1 style={{ marginLeft: -10 }}>
              <Badge color="primary">Rebounds:</Badge>
              50
            </h1>
          </Col>
          <Col>
            <h1>
              <Badge color="primary">Assists:</Badge>
              50
            </h1>
          </Col>
        </Row>
        <Row style={{ marginLeft: 0, marginTop: -30 }}>
          <Col>
            {" "}
            <h1>
              <Badge color="primary">Steals:</Badge>
              50
            </h1>
          </Col>

          <Col>
            {" "}
            <h1>
              <Badge color="primary">Blocks:</Badge>
              50
            </h1>
          </Col>
          <Col>
            <h1>
              <Badge color="primary">PIR:</Badge>
              50
            </h1>
          </Col>
        </Row>
      </div>
    );
  }
}

export default PlayerCompare;
