import React, { Component } from "react";

import {
  Col,
  Row,
  Button,
  Form,
  Card,
  FormGroup,
  Jumbotron,
  Container,
  Label,
  Input,
  FormText,
  InputGroup,
  CardHeader,
  CardBody,
  InputGroupAddon,
  InputGroupText

} from "reactstrap";

import InputRange from "react-input-range";
import { Link, NavLink, withRouter } from "react-router-dom";
import { useState } from "react";

class CreateOption extends Component {

  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {userFriendly: new Boolean()};

  }
  handleSubmit = (e) => {

    this.props.history.push("/admin/searchplayer");
        /*this.setState({
          error: err.response.data.Message,
        });*/
        //this.togglewarningModal();

  };
  render() {
    if (this.state.userFriendly == true) 
    {
      return (




        <Card style= {{background:"transparent",width: "100%" ,height: "100%"}}>


        <Card className="m-7">

          <CardBody className="px-lg-5 ">
            <Form role="form">
              <FormGroup className="mb-3">
                <InputGroup className="input-group-alternative">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-email-83" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Email"
                    type="email"
                    autoComplete="new-email"
                  />
                </InputGroup>
              </FormGroup>
              <FormGroup>
                <InputGroup className="input-group-alternative">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-lock-circle-open" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Password"
                    type="password"
                    autoComplete="new-password"
                  />
                </InputGroup>
              </FormGroup>

              <div className="text-center">
                <Button color="primary" type="button" onClick={this.setState({userFriendly:false})}>
                  Sign in
                </Button>
              </div>
            </Form>
          </CardBody>
        </Card>
        </Card>
        );
    }
    else {
      return(<div> <div className="text-center">
      <Button color="primary" type="button" onClick={this.setState({userFriendly:false})}>
        Sign in
      </Button>
    </div></div>);
    }









  }
}

export default CreateOption;
