import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
import React, { Component } from "react";
import pic from "../assets/img/haland.jpeg";
import aguero from "../assets/img/aguero.png";
import bale from "../assets/img/bale.png";
import firmino from "../assets/img/firmino.jpeg";
import lukaku from "../assets/img/lukaku.jpeg";
import mane from "../assets/img/mane.png";
import messi from "../assets/img/messi.jpeg";
import mesut from "../assets/img/mesut.png";
import ronaldo from "../assets/img/ronaldo.jpeg";
import salah from "../assets/img/salah.jpeg";



class SearchPlayer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      players: [{id:1,pic:pic,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:aguero,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:bale,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:firmino,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:lukaku,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:mane,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:messi,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:mesut,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:ronaldo,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000},
      {id:1,pic:salah,name:"Berke", potential: 92,
      overall: 92,
      age: 20,
      price: 30000000}],
      isLoading: false,
      successModal: false,
      week_id: 1,
      fixture: [],
    };
  }
  imageFormatteraway(cell, row) {
    {
      console.log(row);
    }
    return (
      <img
        alt="..."
        className="rounded-circle"
        src={row.pic}
        width="100"
        height="100"
      />
    );
  }
  addProducts(quantity) {
    const startId = 0;
    for (let i = 0; i < quantity; i++) {
      const id = startId + i;
      this.state.players.push({
        id: id,
        pic: pic,
        name: "Erling Haaland",
        potential: 92,
        overall: 88,
        age: 20,
        price: 2100 + i,
      });
    }
  }

  render() {
    this.addProducts(50);
    console.log(this.state.players);
    return (
      <BootstrapTable
        ref="table"
        bordered={true}
        data={this.state.players}
        pagination={true}
        tableStyle={{ border: "black 2.5px solid" }}
        trStyle={{ color: "#fb6340" ,fontSize:36}}
      >
        <TableHeaderColumn dataField="id" width="10%" isKey={true}>
          <h2 style={{ color: "#5e72e4" }}> Player ID</h2>
        </TableHeaderColumn>
        <TableHeaderColumn
          dataField="pic"
          dataAlign="center"
          dataFormat={this.imageFormatteraway}
          width="20%"
        >
          <h2 style={{ color: "#5e72e4" }}> Player Image</h2>
        </TableHeaderColumn>
        <TableHeaderColumn dataField="name" width="15%" dataAlign="center">
          <h2 style={{ color: "#5e72e4" }}>Player Name</h2>
        </TableHeaderColumn>
        <TableHeaderColumn dataField="potential" width="10%" dataAlign="center">
          {" "}
          <h2 style={{ color: "#5e72e4" }}> Potential</h2>
        </TableHeaderColumn>
        <TableHeaderColumn dataField="overall" width="10%" dataAlign="center">
          {" "}
          <h2 style={{ color: "#5e72e4" }}> Overall</h2>
        </TableHeaderColumn>
        <TableHeaderColumn
          dataField="age"
          width="10%"
          dataAlign="center"
          style={{ color: "#5e72e4" }}
        >
          {" "}
          <h2 style={{ color: "#5e72e4" }}> Age</h2>
        </TableHeaderColumn>
        <TableHeaderColumn dataField="price" width="10%" dataAlign="center">
          {" "}
          <h2 style={{ color: "#5e72e4" }}> Price</h2>
        </TableHeaderColumn>
      </BootstrapTable>
    );
  }
}
export default SearchPlayer;
